import React from 'react'
import {Link, withRouter} from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardMedia from "@material-ui/core/CardMedia";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import CardActions from "@material-ui/core/CardActions";
import IconButton from "@material-ui/core/IconButton";
import VisibilityIcon from '@material-ui/icons/Visibility';
import styled from 'styled-components'
import {makeStyles} from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";

const useStyles = makeStyles(theme => ({
    icon: {
        marginRight: theme.spacing(2),
    },
    heroButtons: {
        marginTop: theme.spacing(4),
    },
    cardMedia: {
        paddingTop: '56.25%', // 16:9
    },
    cardContent: {
        flexGrow: 1,
    },
}));

const CompCardContent = styled(CardContent)`
    padding: 16px 8px 0;
`

const CompCard = styled(Card)`
        height: 100%;
        display: flex;
        flex-direction: column;

        border: 1px solid #fff;
        text-align: center;
        box-shadow: 0 2px 2px 1px rgba(0, 0, 0, .95);
        
        &:hover {
            box-shadow: 0 5px 8px 2px rgba(0, 0, 0, .95);
        }
`

function CompositionCard({info, ...props}) {
    const {id, title, price, thumbnail} = info
    const classes = useStyles()

    return (
        <Grid item xs={12} sm={6} md={4}>
            <CompCard>
                <CardMedia
                    className={classes.cardMedia}
                    image="https://source.unsplash.com/random"
                    title="Image title"
                />
                <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h5" component="h2">
                        Heading
                    </Typography>
                    <Typography>
                        This is a media card. You can use this section to describe the content.
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button size="small" color="primary">
                        View
                    </Button>
                    <Button size="small" color="primary">
                        Edit
                    </Button>
                </CardActions>
            </CompCard>
        </Grid>
    );
}

export default withRouter(CompositionCard)
