import React from 'react'
import {Link, withRouter} from "react-router-dom";
import Grid from "@material-ui/core/Grid";
import Card from "@material-ui/core/Card";
import CardActionArea from "@material-ui/core/CardActionArea";
import CardMedia from "@material-ui/core/CardMedia";
import CardActions from "@material-ui/core/CardActions";
import styled from 'styled-components'
import IconButton from "@material-ui/core/IconButton";
import CompCardContent from "./Content/Content";
import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';

const CompCardPreview = styled(CardMedia)`
    margin: auto;
`

const CompCardWrapper = styled(Card)`
        height: 100%;
        display: flex;
        flex-direction: column;

        border: 1px solid #fff;
        box-shadow: 0 2px 2px 1px rgba(0, 0, 0, .95);
        
        &:hover {
            box-shadow: 0 5px 8px 2px rgba(0, 0, 0, .95);
        }
`

function CompCard({info, ...props}) {
    const {link, title, price, thumb} = info

    return (
        <Grid item xs={12} sm={10} md={6} lg={4} xl={3}>
            <CompCardWrapper>

                <CardActionArea disableRipple>
                    <CompCardPreview component="img" height="320" image={`${process.env.BEP}${thumb}`} alt={title}/>
                </CardActionArea>

                <CompCardContent
                    title={title}
                    price={price}
                />

                <CardActions>
                    {/*<IconButton component={Link} to={`/compositions/${link}`} aria-label="order">*/}
                    {/*    <ShoppingCartIcon fontSize="small"/>*/}
                    {/*</IconButton>*/}
                    {/*<IconButton component={Link} to={`/showcase/${link}`} aria-label="view">*/}
                    {/*    <VisibilityIcon fontSize="small"/>*/}
                    {/*</IconButton>*/}
                </CardActions>

            </CompCardWrapper>
        </Grid>
    );
}

export default withRouter(CompCard)
