import React from 'react';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Switch from "@material-ui/core/Switch";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormGroup from "@material-ui/core/FormGroup";
import {CompVariantCheckbox} from "../../common/CompVariant";
import FormLabel from "@material-ui/core/FormLabel";

function SuggestForm(props) {
    const [state, setState] = React.useState({
        withBackground: false,
        long: false,
        short: false,
    });
    const [value, setValue] = React.useState('');
    const [name, setName] = React.useState('');


    const handleUsername = event => {
        setName(event.target.value);
    };
    const handleDesc = event => {
        setValue(event.target.value);
    };
    const handleChange = name => event => {
        setState({...state, [name]: event.target.checked})
    };

    return (
        <>
            <Grid container spacing={4}>
                <Grid item xs={12}>
                    <TextField
                        required id="username" label="Никнейм"
                        fullWidth value={name} onChange={handleUsername}
                    />
                    <TextField
                        required id="description" label="Описание"
                        fullWidth value={value} onChange={handleDesc}
                        multiline
                    />
                </Grid>
                <Grid item xs={12}>
                    <FormLabel component="legend">Вариант отображения:</FormLabel>
                    <FormGroup row>
                        <CompVariantCheckbox variant='long' handler={handleChange} checked={state.long}/>
                        <CompVariantCheckbox variant='short' handler={handleChange} checked={state.short}/>
                    </FormGroup>
                </Grid>
                <Grid item container xs={12}>
                    <Grid item xs={3}>
                        <FormControlLabel
                            control={
                                <Switch checked={state.withBackground} onChange={handleChange('withBackground')}
                                        value="withBackground" inputProps={{'aria-label': 'default checkbox'}}
                                        color="default"
                                />
                            }
                            label="Фон"
                            labelPlacement="top"
                        />
                    </Grid>
                    <Grid item xs={9}>
                        {state.withBackground && (
                            <TextField required id="background" name="background" label="Адресная ссылка"
                                       fullWidth color="primary"
                            />
                        )}
                    </Grid>
                </Grid>
            </Grid>
        </>
    )
}

export default SuggestForm
