import styled from "styled-components";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import React from "react";
import {createMuiTheme, ThemeProvider, TextField} from "@material-ui/core";

const theme = createMuiTheme({
    overrides: {
        MuiInputLabel: { // Name of the component ⚛️ / style sheet
            root: { // Name of the rule
                color: "orange",
                "&$focused": { // increase the specificity for the pseudo class
                    color: "red",
                        border: "2px solid #337DFD"
                }
            }
        }
    }
});

export function TextInput() {
    return (
        <ThemeProvider theme={theme}>
            <TextField value="value" label="label"/>
        </ThemeProvider>
    );
}

export function Title({text, ...props}) {
    return (
        <Typography component="h1" variant="h5" align="center" color="textPrimary">
            {text}
        </Typography>
    )
}

export const FormWrapper = styled(Container)`
        width: auto;
        margin-left: ${({theme}) => theme.spacing(2)}px;
        margin-right: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(2) * 2)} {
            width: 600px;
            margin-left: auto;
            margin-right: auto;
        };
`

export const InnerFormSpacer = styled(Paper)`
        margin-bottom: ${({theme}) => theme.spacing(3)}px;
        padding: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(3) * 2)} {
            margin-bottom: ${({theme}) => theme.spacing(6)}px;
            padding: ${({theme}) => theme.spacing(3)}px;
        },
`

export const Btn = styled(Button)`
    margin-top: ${({theme}) => theme.spacing(3)}px;
    margin-left: ${({theme}) => theme.spacing(1)}px;
`

export const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-start;
                
        ${(props) => props.left && `justify-content: flex-start;`}
        ${(props) => props.center && `justify-content: center;`}
        ${(props) => props.right && `justify-content: flex-end;`}
`
