export const LOGIN = 'LOGIN'
export const LOGOUT = 'LOGOUT'

const ERR = true

export const login = () => {
    return async (dispatch) => {
        const result = await new Promise((resolve, reject) => {
            if (ERR) reject(false)
            resolve(true)
        })

        dispatch()
        return {
            type: LOGIN,
            payload: false,
        }
    }
}

export function logout() {
    return new Promise((resolve, reject) => {
        resolve(false)
    })
    // return {
    //     type: LOGOUT,
    //     payload: false,
    // }
}
