import * as types from "./types";

const user = {
    user_id: null,
    steamid: null,
    nickname: null,
    avatar: null,
    balance: null,
    group: null,
};

const initialState = {
    isAuthenticated: false,
    token: null,
    user
};

export default function authReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.AUTH_LOGIN:
            return {
                ...state,
            }
        case types.AUTH_CHECK:
            return state = {
                ...state,
            }
        case types.AUTH_LOGOUT:
            return {
                ...state,
            }
        default:
            return state;
    }
};
