import React, {Component} from 'react'
import {withRouter} from "react-router-dom";
import CompList from "./CompList";
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {fetchList, clearList} from "../../../store/ducks/comp-list/operations";
import queryString from 'query-string'
import {ContentWrapper, PageCaption, Pagination, Preloader} from "../../common/layout";
import {handleHTTPError} from "../../../store/ducks/error/operations";


class CompositionsContainer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentPage: null,
            total: null,
            perPage: null,
            countPages: null,
        }
        this.loadCompositions = this.loadCompositions.bind(this)
    }

    componentDidMount() {
        const {compositions, location} = this.props

        if (compositions.length === 0) {
            const search = queryString.parse(location.search)
            if (!(+search.page > 0)) search.page = 1
            this.loadCompositions(null, search.page)

            //
            // const uri = queryString.stringifyUrl({url: location.pathname, query: search});
            //
            // fetchList(uri).then((res) => {
            //     const countPages = Math.ceil(res.total / res.per_page)
            //
            //     this.setState({
            //         ...this.state,
            //         currentPage: +search.page < countPages ? +search.page : 1,
            //         total: res.total,
            //         per_page: res.per_page,
            //         countPages: countPages,
            //     })
            // })
        }
    }

    componentWillUnmount() {
        if (this.props.compositions.length > 0) {
            this.props.clearList()
        }
    }

    loadCompositions(e, to) {
        const {location, fetchList, handleHTTPError} = this.props

        const uri = queryString.stringifyUrl({
            url: location.pathname, query: {
                page: to,
            }
        });

        return fetchList(uri).then(res => {
            const {current_page, per_page, total} = res;

            this.setState({
                ...this.state,
                currentPage: current_page,
                perPage: per_page,
                total: total,
                countPages: Math.ceil(total / per_page)
            })
        }).catch(err => handleHTTPError(err))
    };

    render() {
        const {perPage, total, countPages, currentPage} = this.state

        const pagerNode = (countPages > 1) ? (<Pagination
            count={countPages}
            onChange={this.loadCompositions}
            page={currentPage}/>) : null

        return (
            <SecondaryLayout>
                <PageCaption title="Листинг композиций"/>

                <ContentWrapper>
                    {pagerNode}
                    {
                        this.props.isFetching ?
                            <Preloader/> :
                            <CompList compositions={this.props.compositions}/>
                    }
                    {pagerNode}
                </ContentWrapper>
            </SecondaryLayout>
        )
    }
}

const mapStateToProps = (state) => ({
    compositions: state.compList.compositions,
    isFetching: state.compList.isFetching,
    error: state.error,
})

export default connect(mapStateToProps, {fetchList, clearList, handleHTTPError})(withRouter(CompositionsContainer))
