import React from 'react';
import {Field, withFormik} from 'formik';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {Spacer} from "../../common/layout";
import {FormWrapper, InnerFormSpacer, Title} from "../../common/form_components";
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import {CompVariantCheckbox} from "../../common/CompVariant";
import OrderFormInputs from "./Inputs/Inputs_bckp";

const CustomInputComponent = ({
                                  field, // { name, value, onChange, onBlur }
                                  form: { touched, errors }, // also values, setXXXX, handleXXXX, dirty, isValid, status, etc.
                                  ...props
                              }) => (
    <div>
        <input type="text" {...field} {...props} />
        {touched[field.name] &&
        errors[field.name] && <div className="error">{errors[field.name]}</div>}
    </div>
);

function Variant({id, name, value}) {
    return <Field name="long" component={CustomInputComponent} placeholder="First Name"/>
    // return (
    //     <Field name={name}>
    //         <input
    //             type="checkbox"
    //             id={id}
    //             // checked={field.value}
    //             // {...field}
    //         />
    //     </Field>
    // );
}

const TypeVariants = ({typeVariant, ...props}) => {

    return (
        <Grid container>
            <Grid item xs={12}>
                <Box>
                    <FormLabel component="legend" style={{margin: '1rem 0',}}>Вариант отображения:</FormLabel>
                    <Variant id="long" name="long" value={typeVariant.long}/>
                    {/*<Variant id="short" name="short" value={typeVariant.short} />*/}
                </Box>
            </Grid>
        </Grid>
    )
}


const OrderForm = props => {
    const {
        values, touched, errors, orderFormData,
        handleChange, handleBlur, handleSubmit, handleReset
    } = props;

    return (
        <SecondaryLayout>
            <Spacer/>
            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Title text="Форма заказа"/>
                    <form onSubmit={handleSubmit}>


                        {/*type varient*/}
                        <TypeVariants typeVariant={values.typeVariant}/>

                        {/*inputs*/}

                        <button type="submit">Submit</button>
                        <button onClick={handleReset}>Reset</button>


                    </form>
                </InnerFormSpacer>
            </FormWrapper>
        </SecondaryLayout>
    );
};

const getDefaultValues = (state) => {
    let values = {
        typeVariant: {
            // long: state.typeVariant === 1,
            // short: state.typeVariant === 2,
            long: true,
            short: false,
        }
    }

    // inputs
    Object.keys(state.inputs).forEach(keyName => {
        values[keyName] = ''
    })

    return values
}

const OrderFormContainer = withFormik({
    mapPropsToValues: (props) => (getDefaultValues(props.orderFormData)),

    // Custom sync validation
    validate: values => {
        const errors = {};

        // if (!values.name) {
        //     errors.name = 'Required';
        // }

        return errors;
    },

    handleSubmit: (values, {setSubmitting, ...props}) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
            // props.props.history.push(`${props.props.location.pathname}/success`)
        }, 1000);
    },

    displayName: 'OrderForm',
})(OrderForm);

const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default connect(mapStateToProps, {})(OrderFormContainer)
