import React from 'react'
import ButtonBase from "@material-ui/core/ButtonBase";
import {Link} from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import useStyles from "./Home.style";
import styled from 'styled-components'

const BoxBtn = styled(ButtonBase)`
        will-change: transform;
        width: 450px;
        height: 300px;
        position: relative;
        margin: 3% 3%;
        box-shadow: 0 2px 2px 1px rgba(0, 0, 0, .95);
        transition: all .15s ease-in-out;
        transform: rotate(${({rotate}) => rotate}deg);
`

// imageSrc
const BoxBtnImage = styled.span`
        position: absolute;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        background-size: cover;
        background-position: center 40%;
        background-image: url('${({image}) => image}');
`

//imageBackdrop
const BoxBtnImageBackdrop = styled.span`
        position: absolute;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        opacity: 0.4;
        transition: opacity 300ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
        background-color: #000;
`


const StyledButton = styled.button`
  ${({theme}) => `
  padding: 8px 12px;
  border: 1px solid;
  cursor: pointer;
  outline: 0;
  border-radius: ${theme.shape.borderRadius}px;
  color: ${theme.palette.primary.contrastText};
  background-color: ${theme.palette.primary.main};
  border-color: ${theme.palette.primary.main};
  transition: ${theme.transitions.create(['background-color', 'box-shadow'])};
  font-family: ${[
    '-apple-system',
    'BlinkMacSystemFont',
    '"Segoe UI"',
    'Roboto',
    '"Helvetica Neue"',
    'Arial',
    'sans-serif',
    '"Apple Color Emoji"',
    '"Segoe UI Emoji"',
    '"Segoe UI Symbol"',
].join(',')};
  &:hover {
    background-color: ${darken(theme.palette.primary.main, 0.1)};
    border-color: ${darken(theme.palette.primary.main, 0.2)};
  }
  &:active {
    background-color: ${darken(theme.palette.primary.main, 0.2)};
    border-color: ${darken(theme.palette.primary.main, 0.3)};
  }
  &:focus {
    box-shadow: 0 0 0 0.2rem ${fade(theme.palette.primary.main, 0.5)};
  }
  font-size: 18px;
  ${theme.breakpoints.up('md')} {
    font-size: 16px;
  }
  `}
`;

const BoxVariant = React.memo(({image, ...props}) => {
    const classes = useStyles();

    return (
        <BoxBtn component={Link} to={image.to}
                className={classes.image}
                rotate={image.rotate}
                focusRipple
                p={5}
        >
            <BoxBtnImage image={image.url}/>
            <BoxBtnImageBackdrop className={classes.imageBackdrop}/>
            <span className={classes.imageButton}>
            <Typography
                component="span"
                variant="subtitle1"
                color="inherit"
                className={classes.imageTitle}
            >
              {image.title}
            </Typography>
          </span>
        </BoxBtn>
    )
})

export default BoxVariant
