import React from 'react';
import Header from "../_includes/header/Header";
import Drawer from "../_includes/drawer/Drawer";
import Content from "../_includes/content/Content";
import Footer from "../_includes/footer/Footer";
import {connect} from "react-redux";
import Background from "./Background";
import {withRouter} from "react-router-dom";

const Layout = React.memo((props) => {

    const {user} = props

    return (
        <>
            <Background>
                <Header user/>
                {(user.isAuthenticated && props.location.pathname !== '/') && <Drawer/>}
                <Content>
                    {props.children}
                </Content>
            </Background>
        </>
    );
})

const mapStateToProps = (store) => ({
    user: store.user
})

export default connect(mapStateToProps)(withRouter(Layout))
