import React from 'react';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import Container from "@material-ui/core/Container";
import styled from "styled-components";
import Switch from "@material-ui/core/Switch";
import FormControlLabel from "@material-ui/core/FormControlLabel";

const Btn = styled(Button)`
    margin-top: ${({theme}) => theme.spacing(3)}px;
    margin-left: ${({theme}) => theme.spacing(1)}px;
`

const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-end;
`

const InnerFormSpacer = styled(Paper)`
        margin-bottom: ${({theme}) => theme.spacing(3)}px;
        padding: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(3) * 2)} {
            margin-bottom: ${({theme}) => theme.spacing(6)}px;
            padding: ${({theme}) => theme.spacing(3)}px;
        },
`

const SuggestWrapper = styled(Container)`
        width: auto;
        margin-left: ${({theme}) => theme.spacing(2)}px;
        margin-right: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(2) * 2)} {
            width: 600px;
            margin-left: auto;
            margin-right: auto;
        };
`

const PageCaptionSpacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`

function SuggestForm() {
    const [state, setState] = React.useState({
        withBackground: false,
    });

    const handleChange = name => event => {
        setState({...state, [name]: event.target.checked});
    };

    // description, type (two checkboxes), background url input (if checkbox true)
    return (
        <>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <TextField required id="description" name="description" label="Description"
                               fullWidth autoComplete="Description" color="primary"
                    />
                    {state.withBackground && (
                        <TextField required id="background" name="background" label="Steam background url"
                                   fullWidth autoComplete="Background" color="primary"
                        />
                    )}

                    <FormControlLabel
                        control={
                            <Switch checked={state.withBackground} onChange={handleChange('withBackground')}
                                    value="withBackground" inputProps={{'aria-label': 'secondary checkbox'}}
                                    color="default"
                            />
                        }
                        label="Secondary"
                    />
                </Grid>
            </Grid>
        </>
    )
}

function SuggestContainer() {

    return (
        <SecondaryLayout>
            <PageCaptionSpacer>
                <Container maxWidth="xl">
                    <Typography component="h1" variant="h5" align="left" color="textPrimary">
                        Письмо дизайнерам
                    </Typography>
                </Container>
            </PageCaptionSpacer>

            <SuggestWrapper maxWidth="xl">
                <InnerFormSpacer>

                    <SuggestForm/>
                    <ButtonsGroup>
                        <Btn variant="contained">Предложить</Btn>
                    </ButtonsGroup>

                </InnerFormSpacer>
            </SuggestWrapper>

        </SecondaryLayout>
    );
}

export default SuggestContainer
