import React from 'react';
import {useStyles} from "./Home.style";
import {withTheme} from '@material-ui/core/styles';
import {Link} from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import ButtonBase from "@material-ui/core/ButtonBase";
import Box from "@material-ui/core/Box";
import {connect} from "react-redux";

import Header from "../_layouts/_includes/header/Header";
import Content from "../_layouts/_includes/content/Content";
import Footer from "../_layouts/_includes/footer/Footer";


function HomePage(props) {
    const classes = useStyles();
    const theme = props.theme

    return (
        <div className={classes.root} style={{background: theme.palette.primary.mainGradient}}>
            {/* eslint-disable-next-line react/jsx-no-undef */}
            <Header/>
            <Content title="Предложи свою идею для создания композиции или зацени уже имеющиеся!">
                <Box display="flex" justifyContent="center" m={1} p={1}>
                    {props.images.map(image => {
                        return (
                            <Box key={image.title} m={5}>
                                <ButtonBase
                                    component={Link} to={image.to}
                                    focusRipple
                                    className={classes.image}
                                    focusVisibleClassName={classes.focusVisible}
                                    p={1}
                                    style={{transform: `rotate(${image.rotate}deg)`}}>
                                          <span
                                              className={classes.imageSrc}
                                              style={{
                                                  backgroundImage: `url(${image.url})`,
                                              }}
                                          />
                                    <span className={classes.imageBackdrop}/>
                                    <span className={classes.imageButton}>
                                            <Typography
                                                component="span"
                                                variant="subtitle1"
                                                color="inherit"
                                                className={classes.imageTitle}
                                            >{image.title}</Typography>
                                        </span>
                                </ButtonBase>
                            </Box>
                        )
                    })}
                </Box>
            </Content>
            <Footer/>
        </div>
    );
}

const mapStateToProps = (state) => {
    return {
        images: state.homePage.images
    }
}

const mapDispatchToProps = (dispatch) => {
    return {}
}

export default connect(mapStateToProps)(
    withTheme(HomePage)
)
