import React, {Component} from 'react'
import Typography from "@material-ui/core/Typography";
import {withRouter} from "react-router-dom";
import CompositionList from "./CompositionList";
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import Container from "@material-ui/core/Container";
import styled from "styled-components";
import {fetchList, clearList} from "../../../store/ducks/compositions/operations";
import Preloader from "../../common/Preloader";
import queryString from 'query-string'
import Pagination from "../../common/Pagination";


const CompListWrapper = styled(Container)`
        padding-top: ${({theme}) => theme.spacing(2)}px;
        padding-bottom: ${({theme}) => theme.spacing(2)}px;
        background-color: rgba(33,33,33,.95);
        border-radius: 20px;
`

const PageCaptionSpacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`

class CompositionsContainer extends Component {
    constructor(props) {
        super(props)
        this.state = {
            currentPage: null,
            total: null,
            per_page: null,
            countPages: null,
        }
        this.loadCompositions = this.loadCompositions.bind(this)
    }

    componentDidMount() {
        const {compositions, location} = this.props

        if (compositions.length === 0) {
            const search = queryString.parse(location.search)
            if (!(+search.page > 0)) search.page = 1
            this.loadCompositions(search.page).then((res) => {
                this.setState({
                    ...this.state,
                    ...res
                })
            })

            //
            // const uri = queryString.stringifyUrl({url: location.pathname, query: search});
            //
            // fetchList(uri).then((res) => {
            //     const countPages = Math.ceil(res.total / res.per_page)
            //
            //     this.setState({
            //         ...this.state,
            //         currentPage: +search.page < countPages ? +search.page : 1,
            //         total: res.total,
            //         per_page: res.per_page,
            //         countPages: countPages,
            //     })
            // })
        }
    }

    componentWillUnmount() {
        if (this.props.compositions.length > 0) {
            this.props.clearList()
        }
    }

    loadCompositions(to) {
        const {location, fetchList} = this.props

        const uri = queryString.stringifyUrl({
            url: location.pathname, query: {
                page: to,
            }
        });

        return fetchList(uri).then(res => {
            return {
                current_page: res.current_page,
                per_page: res.per_page,
                total: res.total,
                count_pages: Math.ceil(res.total / res.per_page)
            }
        })
    };

    render() {
        // console.log(this.state)
        const pagerNode = (
            <Pagination
                countPages={this.state.countPages}
                handlePageChange={this.loadCompositions}
                currentPage={this.state.currentPage}/>
        )

        return (
            <SecondaryLayout>
                <PageCaptionSpacer>
                    <Container maxWidth="xl">
                        <Typography component="h1" variant="h5" align="left" color="textPrimary">
                            Листинг композиций
                        </Typography>
                    </Container>
                </PageCaptionSpacer>

                <CompListWrapper maxWidth="xl">
                    {pagerNode}
                    {
                        this.props.isFetching ?
                            <Preloader/> :
                            <CompositionList compositions={this.props.compositions}/>
                    }
                    {pagerNode}
                </CompListWrapper>

            </SecondaryLayout>
        )
    }
}

const mapStateToProps = (store) => ({
    compositions: store.compositions.compositions,
    isFetching: store.compositions.isFetching,
    total: store.compositions.total,
    per_page: store.compositions.per_page,
})

export default connect(mapStateToProps, {fetchList, clearList})(withRouter(CompositionsContainer))
