import {
    executeErrorHandler, resetErrorHandler
} from "./actions";
import {logout} from "../auth/operations";

const handleHTTPError = (error) => dispatch => {
    if (error.status !== 401) {
        return dispatch(executeErrorHandler(error))
    } else {
        return dispatch(logout())
    }
}

const resetHTTPErrors = () => dispatch => {
    return dispatch(resetErrorHandler())
}

export {
    handleHTTPError, resetHTTPErrors
}
