import {AxiosInstance as Http} from "axios";
import * as types from "./types";

const user = {
    user_id: null,
    steamid: null,
    nickname: null,
    avatar: null,
    balance: null,
    group: null
};

const initialState = {
    isAuthenticated: false,
    user
};

function authReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.AUTH_LOGIN:
            // Http.defaults.headers.common['Authorization'] = `Bearer ${jwtToken}`;
            console.log(payload.isAuthenticated)
            return {
                ...state,
                isAuthenticated: payload.isAuthenticated,
                user: {
                    user_id: payload.user.user_id,
                    steamid: payload.user.steamid,
                    nickname: payload.user.nickname,
                    avatar: payload.user.avatar,
                    balance: payload.user.balance,
                    group: payload.user.group,
                }
            }
        // case types.AUTH_CHECK:
        //     state = {
        //         ...state,
        //         isAuthenticated: !!localStorage.getItem('jwt_token'),
        //     }
        //
        //     if (state.isAuthenticated) {
        //         Http.defaults.headers.common['Authorization'] = `Bearer ${localStorage.getItem('jwt_token')}`;
        //     }
        //
        //     return state;
        case types.AUTH_LOGOUT:
            const user = {
                user_id: null,
                steamid: null,
                nickname: null,
                avatar: null,
                balance: null,
                group: null
            }

            return {
                ...state,
                isAuthenticated: false,
                user
            }
        default:
            return state;
    }
};

export default authReducer
