import React from 'react'
import ButtonBase from "@material-ui/core/ButtonBase";
import {Link} from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import useStyles from "./Home.style";
import styled from 'styled-components'

const BoxBtn = styled(ButtonBase)`
        will-change: transform;
        width: 450px;
        height: 300px;
        position: relative;
        margin: 3% 3%;
        box-shadow: 0 2px 2px 1px rgba(0, 0, 0, .95);
        transition: all .15s ease-in-out;
        transform: rotate(${({rotate}) => rotate}deg);
        
        &:hover {
            box-shadow: 0 5px 8px 2px rgba(0, 0, 0, .95);
            z-index: 1;
            & $imageBackdrop {
                opacity: 0.15;
            },
            & $imageTitle {
                border: 4px solid currentColor;
            },
        }
        &:active {
            color: rgba(255, 255, 255, .95);
            box-shadow: 0 5px 8px 2px rgba(243, 156, 18, .95);
            transition: all .75s ease-in-out;
        }
        
`

const BoxVariant = React.memo(({image, ...props}) => {
    const classes = useStyles();

    return (
        <BoxBtn component={Link} to={image.to}
                rotate={image.rotate}
                focusRipple
                p={5}
        >
          <span
              className={classes.imageSrc}
              style={{
                  backgroundImage: `url(${image.url})`,
              }}
          />
            <span className={classes.imageBackdrop}/>
            <span className={classes.imageButton}>
            <Typography
                component="span"
                variant="subtitle1"
                color="inherit"
                className={classes.imageTitle}
            >
              {image.title}
            </Typography>
          </span>
        </BoxBtn>
    )
})

export default BoxVariant
