import {authLogout, authLogin} from "./actions";

const login = (response) => {

    return dispatch => (
        new Promise((resolve, reject) => {
            if (response.success) {
                const {data: creds} = response
                dispatch(authLogin(creds));
                return resolve();
            } else {
                return reject();
            }
        })
    )

}

const logout = () => {
    return dispatch => (
        new Promise((resolve) => {
            // axios.post('api/auth/logout')
            //     .then((res) => {
            //         dispatch(authLogout())
            //         return resolve(res.data)
            //     })
        })
    )
}

export {
    login, logout
}
