import {
    authLogin,
    authLogout,
    authLoginCompleted,
    authLogoutCompleted,
    authLoginFailed,
    authLogoutFailed
} from "./actions";
import {fetch} from "../../utils";
import {handleHTTPError} from "../error/operations";

const login = (params) => (dispatch) => {
    dispatch(authLogin())
    return fetch(`/airlock/csrf-cookie`, "GET")
        .then(() => {
            fetch(`/api/login/steam/callback${params}`, "GET")
                .then((user) => {
                    dispatch(authLoginCompleted(user))
                })
                .catch((err) => {
                    dispatch(authLoginFailed())
                    dispatch(handleHTTPError(err))
                });
        })
        .catch(err => {
            dispatch(authLoginFailed())
            dispatch(handleHTTPError(err))
        });
}

const logout = () => dispatch => {
    dispatch(authLogout())
    return fetch(`/api/logout`, "POST")
        .then((res) => {
            dispatch(authLogoutCompleted())
        })
        .catch(err => {
            dispatch(authLogoutFailed())
            dispatch(handleHTTPError(err))
        });
}

const me = () => dispatch => {
    dispatch(authLogin())
    return fetch(`/api/user`, "GET")
        .then((response) => {
            dispatch(authLoginCompleted(response))
            return response
        })
        .catch((err) => {
            console.log(err)
            // dispatch(logout())
            // dispatch(handleHTTPError(err))
        });
}


export {
    login, logout, me
}
