import React from "react";
import styled from "styled-components";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import {TextField} from "@material-ui/core";
import Checkbox from "@material-ui/core/Checkbox";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Icon from "@material-ui/core/Icon";
import LongCompDefault from '../../assets/long_icon.svg'
import LongCompActive from '../../assets/long_icon_active.svg'
import ShortCompDefault from '../../assets/short_icon.svg'
import ShortCompActive from '../../assets/short_icon_active.svg'
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import {Field, getIn} from "formik";
import Alert from "@material-ui/lab/Alert";
import Collapse from "@material-ui/core/Collapse";
import {InnerPreloader, Spacer} from "./layout";

export const TextInput = styled(TextField)`
  label.Mui-focused {
    color: white;
  }
  .MuiOutlinedInput-root {
    border-radius: 0;
    &:hover fieldset {
      border-color: white;
    }
    &.focused fieldset {
      border-color: white;
    }
  }
  .MuiOutlinedInput-root.Mui-focused fieldset {
    border-color: white;
  }
`;
// #4CAF50 green
// #ef5350 red

export function Title({text, ...props}) {
    return (
        <Typography component="h1" variant="h5" align="center" color="textPrimary" gutterBottom={true}>
            {text}
        </Typography>
    )
}

export const Wrapper = styled(Container)`
        width: auto;
        margin-left: ${({theme}) => theme.spacing(2)}px;
        margin-right: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(2) * 2)} {
            width: 600px;
            margin-left: auto;
            margin-right: auto;
        };
`

export const FormWrapper = ({caption = "Безымянный", size = "xl", children, ...props}) => {
    return (
        <>
            <Spacer/>
            <Wrapper maxWidth={size}>
                <InnerFormSpacer>
                    <Title text={caption}/>
                    {children}
                </InnerFormSpacer>
            </Wrapper>
        </>
    )
}


export const InnerFormSpacer = styled(Paper)`
        margin-bottom: ${({theme}) => theme.spacing(3)}px;
        padding: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(3) * 2)} {
            margin-bottom: ${({theme}) => theme.spacing(6)}px;
            padding: ${({theme}) => theme.spacing(3)}px;
        },
`

export const Btn = styled(Button)`
    margin-top: ${({theme}) => theme.spacing(3)}px;
    margin-left: ${({theme}) => theme.spacing(1)}px;
`

export const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-start;
                
        ${(props) => props.left && `justify-content: flex-start;`}
        ${(props) => props.center && `justify-content: center;`}
        ${(props) => props.right && `justify-content: flex-end;`}
`

export function CompIcon(props) {
    return (
        <Icon style={{textAlign: 'center', fontSize: '7.5rem',}}>
            <img {...props} height="100%" width="100%" alt="Composition Icon"/>
        </Icon>
    );
}

export const CompVariantCB = ({disabled = false, ...props}) => {
    const icon = props.value === 'long' ? LongCompDefault : ShortCompDefault
    const checkedIcon = props.value === 'long' ? LongCompActive : ShortCompActive

    return (
        <Field name={props.name}>
            {({field, form}) => (
                <FormControlLabel
                    control={
                        <Checkbox
                            {...props}
                            icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                            color="default" style={{borderRadius: 0,}}
                            checked={field.value.includes(props.value)}
                            onChange={form.handleChange} onBlur={form.handleBlur}
                        />
                    }
                    label={props.label}
                    labelPlacement="bottom"
                    disabled={disabled}
                />
            )}
        </Field>
    )
}

export const CompVariantRB = ({disabled = false, ...props}) => {
    const icon = props.value === 'long' ? LongCompDefault : ShortCompDefault
    const checkedIcon = props.value === 'long' ? LongCompActive : ShortCompActive

    return (
        <Field name={props.name}>
            {({field, form}) => {
                return (
                    <FormControlLabel
                        control={
                            <Radio
                                {...props}
                                icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                                color="default" style={{borderRadius: 0,}}
                                checked={field.value === props.value}
                                onChange={form.handleChange} onBlur={form.handleBlur}
                            />
                        }
                        label={props.label}
                        labelPlacement="bottom"
                        disabled={disabled}
                    />
                )
            }}
        </Field>
    );
};

export const TypeVariantsCB = ({touched, errors, isFetching, ...props}) => {
    return (
        <TypeVariantsWrapper
            touched={touched}
            errors={errors}
            isFetching={isFetching}
        >
            <CompVariantCB name="typeVariant" value="long" label="Long"/>
            <CompVariantCB name="typeVariant" value="short" label="Short"/>
        </TypeVariantsWrapper>
    )
}

export const TypeVariantsRB = ({variant, touched, errors, isFetching, ...props}) => {
    return (
        <TypeVariantsWrapper
            touched={touched}
            errors={errors}
            isFetching={isFetching}
        >
            <CompVariantRB name="typeVariant" value="long" label="Длинный"
                           disabled={variant === 3}/>
            <CompVariantRB name="typeVariant" value="short" label="Короткий"
                           disabled={variant === 2}/>
        </TypeVariantsWrapper>
    )
}

export const TypeVariantsWrapper = ({touched, errors, isFetching, ...props}) => {
    return (
        <Grid item xs={12}>
            <Box style={{backgroundColor: '#303030'}} p={1}>
                <FormLabel component="legend" style={{margin: '1rem 0', textAlign: 'center',}}>
                    Variant:
                </FormLabel>

                <FormGroup row style={{justifyContent: 'center',}}>
                    {
                        !!isFetching ?
                            <>
                                <InnerPreloader/>
                                <InnerPreloader/>
                            </>
                            :
                            props.children
                    }
                </FormGroup>

                <Collapse in={!!errors["typeVariant"] && !!touched["typeVariant"]}>
                    <Alert variant="filled" severity="warning">{errors["typeVariant"]}</Alert>
                </Collapse>
            </Box>
        </Grid>
    )
}

export const PlaceholderList = ({inputs, ...props}) => {
    return Object.keys(inputs).map((inputName, id) => (
            <Placeholder
                key={id}
                name={`ph.${inputName}`}
                label={inputs[inputName].label}
            />
        )
    )
}

export const Placeholder = (props) => {
    return (
        <Grid item xs={12}>
            <Field name={props.name} label={props.label}>
                {({field, form}) => (
                    <Box mt={2} style={{backgroundColor: '#303030'}}>
                        <TextInput
                            required variant="outlined" fullWidth
                            onChange={form.handleChange}
                            {...field} {...props}
                        />

                        <Collapse in={getIn(form.errors, field.name) && getIn(form.touched, field.name)}>
                            <Alert variant="filled" severity="warning">{getIn(form.errors, field.name)}</Alert>
                        </Collapse>
                    </Box>
                )}
            </Field>
        </Grid>
    );
};
