import React, {Component} from 'react'
import Layout from "../_layouts/default/Layout";
import Typography from "@material-ui/core/Typography";
import axios from 'axios';
import {withRouter} from "react-router-dom";
import CompositionList from "./CompositionList";
import Pager from "./Pager";
import RCompThumbnail from '../../assets/img/compositions/static.png'
import RCompPreview from '../../assets/img/compositions/dynamic.gif'

class CompositionsContainer extends Component {

    constructor(props) {
        super(props)
        this.state = {
            limit: 2,
            compositions: [],
            pageCount: 0,
            offset: 0,
        }
    }

    loadCommentsFromServer() {
        setTimeout(() => {
            const compositions = [{
                id: 0,
                title: 'Fear',
                thumbnail: RCompThumbnail,
                preview: RCompPreview,
                dateCreated: new Date(),
                author: 'Vitaly',
                type: 'any',
                background: '',
            }]
            this.setState({
                compositions: compositions,
                pageCount: Math.ceil(compositions.length / this.state.limit),
            });
        }, 1000)
        // axios.get('/compositions', {
        //     data: {
        //         limit: 16,
        //         offset: this.state.offset
        //     },
        // }).then(data => {
        //     this.setState({
        //         compositions: data.compositions,
        //         pageCount: Math.ceil(this.state.compositions.length / this.state.limit),
        //     });
        // }).catch((error) => {
        //     console.log(error);
        // })
    }

    componentDidMount() {
        // console.log(this.props)
        // const page = this.props.match.params.page
        this.loadCommentsFromServer();
    }

    handlePageClick = data => {
        let selected = data.selected;
        let offset = Math.ceil(selected * 4);
        // const page = this.props.match.params.page

        this.setState({offset: offset}, () => {
            this.loadCommentsFromServer();
        });
    }

    render() {
        return (
            <Layout>
                <Typography variant="h5" gutterBottom>Листинг композиций</Typography>
                <CompositionList compositions={this.state.compositions}/>
                <Pager pageCount={this.state.pageCount} onPageChange={this.handlePageClick}/>
            </Layout>
        )
    }
}

export default withRouter(CompositionsContainer)
