import React from 'react';
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import CssBaseline from "@material-ui/core/CssBaseline";
import useMediaQuery from "@material-ui/core/useMediaQuery/useMediaQuery";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import {ThemeProvider} from '@material-ui/core/styles';
import {ProtectedRoute} from "./ProtectedRoute";

import Home from "./pages/home/Home";
import Compositions from "./pages/compositions/Compositions";
import NotFound from "./pages/error/NotFound";

function App() {
    const prefersDarkMode = useMediaQuery('(prefers-color-scheme: dark)');

    const theme = React.useMemo(
        () =>
            createMuiTheme({
                palette: {
                    type: prefersDarkMode ? 'light' : 'dark',
                    primary: {
                        main: '#3f51b5',
                        mainGradient: "linear-gradient(310deg, rgb(192, 192, 192) 70%, rgb(250, 250, 250) 100%)}",
                        lightGradient: "linear-gradient(310deg, rgba(46, 204, 113, 1), rgb(52, 152, 219))",
                        darkGradient: "linear-gradient(to right, #333, #555)",
                    },
                },
            }),
        [prefersDarkMode],
    );

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline/>
            <Router>
                <Switch>
                    <Route exact path="/" component={Home}/>
                    <Route path="/compositions" component={Compositions}/>
                    <Route path="/suggest" component={() => <div>suggest form</div>}/>
                    <Route path="*" component={NotFound}/>
                </Switch>
            </Router>
        </ThemeProvider>
    );
}

export default App
