import * as types from "./types";
import UserApi from "../../utils/UserApi"

const user = {
    id: null,
    nickname: null,
    steamid: null,
    avatar: null
};

const initialState = {
    user,
    isLoading: false,
    error: null
};

export default function authReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.STEAM_LOGIN:
            return {
                ...state,
                isLoading: true,
            };
        case types.STEAM_LOGIN_COMPLETED:
            return authLogin(state, payload);
        case types.STEAM_LOGIN_FAILED:
            return {
                ...state,
                isLoading: false,
            };
        case types.STEAM_LOGOUT:
            return {
                ...state,
                isLoading: true
            };
        case types.STEAM_LOGOUT_COMPLETED:
            return authLogout(state, payload);
        case types.STEAM_LOGOUT_FAILED:
            return {
                ...state,
                isLoading: false
            };
        default:
            return state;
    }
};

const authLogin = (state, payload) => {
    const user = payload;
    UserApi.authenticate()

    return {
        ...state,
        isLoading: false,
        user: {
            ...user
        }
    }
};

const authLogout = (state, payload) => {
    UserApi.logout(() => {
        console.log('hai')
    })

    return {
        ...state,
        isLoading: false,
        user
    }
};
