import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import Container from "@material-ui/core/Container";
import styled from "styled-components";

const useStyles = makeStyles(theme => ({
    layout: {
        width: 'auto',
        marginLeft: theme.spacing(2),
        marginRight: theme.spacing(2),
        [theme.breakpoints.up(600 + theme.spacing(2) * 2)]: {
            width: 600,
            marginLeft: 'auto',
            marginRight: 'auto',
        },
    },
    paper: {
        marginBottom: theme.spacing(3),
        padding: theme.spacing(2),
        [theme.breakpoints.up(600 + theme.spacing(3) * 2)]: {
            marginBottom: theme.spacing(6),
            padding: theme.spacing(3),
        },
    },
    button: {
        marginTop: theme.spacing(3),
        marginLeft: theme.spacing(1),
    },
}));

const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-end;
`

const PageCaptionSpacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`

const SuggestWrapper = styled(Container)`
        padding-top: ${({theme}) => theme.spacing(8)}px;
        padding-bottom: ${({theme}) => theme.spacing(8)}px;
`

function SuggestForm() {
    // description, type (two checkboxes), background url input (if checkbox true)
    return (
        <>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <TextField
                        required
                        id="description"
                        name="description"
                        label="Description"
                        fullWidth
                        autoComplete="Description"
                        color="secondary"
                    />
                </Grid>
            </Grid>
        </>
    )
}

function SuggestContainer() {
    const classes = useStyles();

    return (
        <SecondaryLayout>
            <PageCaptionSpacer>
                <Container maxWidth="xl">
                    <Typography component="h1" variant="h5" align="left" color="textPrimary">
                        Письмо дизайнерам
                    </Typography>
                </Container>
            </PageCaptionSpacer>

            <SuggestWrapper maxWidth="xl">
                <div className={classes.layout}>
                    <Paper className={classes.paper}>
                        <SuggestForm/>
                        <ButtonsGroup>
                            <Button variant="contained" className={classes.button}>Предложить</Button>
                        </ButtonsGroup>
                    </Paper>
                </div>
            </SuggestWrapper>

        </SecondaryLayout>
    );
}

export default SuggestContainer
