import styled from "styled-components";
import Container from "@material-ui/core/Container";
import Typography from "@material-ui/core/Typography";
import React from "react";
import {Pagination as MaterialPagination} from "@material-ui/lab";
import PaginationItem from "@material-ui/lab/PaginationItem";
import {Link} from "react-router-dom";
import CircularProgress from "@material-ui/core/CircularProgress";

export const ToolbarSpacer = styled.div`
    toolbar: ${({theme}) => theme.mixins.toolbar};
`

export const Spacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`


export const PageCaption = styled((props) => {
    return (<Spacer>
        <Container maxWidth="xl">
            <Typography component="h1" variant="h5" align="left" color="textPrimary">
                {props.title}
            </Typography>
        </Container>
    </Spacer>)
})``

export const ContentWrapper = styled(Container).attrs({
    maxWidth: 'xl',
})`
        padding-top: ${({theme}) => theme.spacing(1)}px;
        padding-bottom: ${({theme}) => theme.spacing(1)}px;
        background-color: rgba(33,33,33,.95);
        border-radius: 20px;
`


export const Pagination = styled(({className, spacing = 2, ...props}) => {
    return (
        <div
            className={className}
            spacing={spacing}
        >
            <MaterialPagination {...props} />
        </div>
    )
})
    .attrs({
        renderItem: (item) => (
            <PaginationItem
                component={Link}
                to={`/compositions${item.page === 1 ? '' : `?page=${item.page}`}`}
                {...item}
            />)
    })`
        padding: ${({theme, ...props}) => props.spacing}px 0;
    `


export const PagePreloader = styled.div`
    display: block;
    position: fixed;
    z-index: 99999;
    top: 0;
    left: 0;
    width 100%;
    height: 100%;
    min-width: 1000px;
    background-color: rgba(33, 33, 33, .95);
    background-image: url("http://hello-site.ru//main/images/preloads/tail-spin.svg");
    background-position: center center;
    background-repeat: no-repeat;
    background-size: 96px;
`

export const Preloader = styled.div`
    display: block;
    position: relative;
    width: 100%;
    min-height: 96px;
    background-image: url("http://hello-site.ru//main/images/preloads/tail-spin.svg");
    background-position: center center;
    background-repeat: no-repeat;
    background-size: 96px;
`

export const InnerPreloader = styled(({size = 48, ...props}) => {
    return <div className={props.className}>
        <CircularProgress size={size} color="inherit"/>
    </div>
})`
display: inline-block;
margin: 0px ${({theme, sideMargin = 4}) => theme.spacing(sideMargin)}px;
`

