import {authLogin, authLogout, authMe} from "./actions";
import {fetch} from "../../utils";

const login = response => dispatch => {
    return new Promise((resolve, reject) => {
        if (response.success) {
            const {data: creds} = response
            dispatch(authLogin(creds));
            return resolve()
        } else {
            return reject()
        }
    })
}

const logout = () => dispatch => {
    return fetch('/api/auth/logout', "POST").then(() => {
        dispatch(authLogout())
    })
}

const me = () => dispatch => {
    return fetch('/api/auth/me', "POST")
        .then((user) => {
            console.log('b')
            dispatch(authMe(user))
        })
        .catch(err => {
            console.error(err)
        })
}

const refresh = (token = null) => (dispatch, getState) => {
    if (!!token && getState().auth.isAuthenticated) {
        // если данные о пользователе в стейте, тогда запрос за новым токеном (в теле - старый),
        return fetch('/api/auth/refresh', "POST")
            .then((data) => {
                console.log(data)
                return me()
            })
            .catch(err => {
                console.error(err)
            })
    } else {
        return logout()
    }
}

export {
    login, logout, me, refresh
}
