        <footer className={classes.footer}>
            <Container maxWidth="sm">
                <Box display="flex" justifyContent="center" alignItems="center">
                    <Typography variant="body1"><q>{currPhrase.author}</q>&ndash;
                        <cite>{currPhrase.text}</cite>.
                    </Typography>
                </Box>
            </Container>
            <Container maxWidth="sm">
                <Box display="flex" justifyContent="center" alignItems="center">
                    <Copyright/>
                </Box>
            </Container>
        </footer>
    )

