import React from 'react';
import CssBaseline from "@material-ui/core/CssBaseline";
import {ThemeProvider as MuiThemeProvider} from '@material-ui/core';
import {ThemeProvider} from 'styled-components';
import {ApplyRootTheme} from "./views/layout/theme";
import Routes from "./routes";
import {connect} from "react-redux";
import {initApp} from "./store/ducks/app/operations";
import {PagePreloader} from "./views/common/layout";
import {SnackbarProvider} from "notistack";


class Root extends React.Component {

    componentDidMount() {
        this.props.initApp()
    }

    render() {
        const theme = ApplyRootTheme()

        return (
            <MuiThemeProvider theme={theme}>
                <ThemeProvider theme={theme}>
                    <SnackbarProvider maxSnack={3} style={{
                        success: { backgroundColor: 'white' },
                        error: { color: 'white' },
                    }}>
                        <CssBaseline/>

                        {!this.props.initialized ? <PagePreloader/> : <Routes/>}

                    </SnackbarProvider>
                </ThemeProvider>
            </MuiThemeProvider>
        );
    }
}


const mapStateToProps = (state) => ({
    initialized: state.app.initialized
})

export default connect(mapStateToProps, {initApp})(Root)
