import React from 'react'
import {fetch} from "../../../store/utils";
import axios from 'axios'

class SteamAuth extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loading: true,
            error: null,
            data: {},
        };
    }

    componentDidMount() {
        fetch(`/airlock/csrf-cookie`, "GET")
            .then(res => {
                console.log(res)
                // fetch(`/api/login/steam/callback${this.props.location.search}`, "GET")
                //     .then((data) => {
                //         this.setState({loading: false, data});
                //     })
                //     .catch((error) => {
                //         this.setState({loading: false, error});
                //         console.error(error);
                //     });
            })
            .catch(err => console.log(err));
    }

    render() {
        const {loading, error, data} = this.state;

        if (loading) {
            return <div>Loading....</div>;
        }

        if (error) {
            return (
                <div>
                    <p>Error:</p>
                    <code className="Code-block">{error.toString()}</code>
                </div>
            );
        } else {
            console.log(data)
        }

        return (
            <details>
                <summary>Welcome</summary>
                <p>Here is your info: </p>
                <code className="Code-block">{JSON.stringify(data, null, 2)}</code>
            </details>
        );
    }
}

export default SteamAuth;
