            // };
            // if (statusCode === 401 || statusCode === 422) {
            //     // status 401 means unauthorized
            //     // status 422 means unprocessable entity
            //     data.error = err.response.data.message;
            // }
            // return reject(data);
        })
    )
}


export {
    login, logout
}
