import React from 'react';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import PageCaptionSpacer from "../common/PageCaptionSpacer";
import Typography from "@material-ui/core/Typography";
import {FormWrapper, InnerFormSpacer} from "../common/form_components";
import CompFormInputs from "./CompFormInputs/CompFormInputs";
import CompFormSummarize from "./CompFormSummarize/CompFormSummarize";
import CompFormControls from "./CompFormControls/CompFormControls";


function CompFormContainer(props) {
    const [state, setState] = React.useState({
        typeVariant: {
            long: false,
            short: false,
        },
        inputs: {
            username: '',
            channel_name: '',
        },
    });


    const handleUsernameInput = event => {
        setState({
            ...state,
            inputs: {...state.inputs, username: event.target.value,}
        });
        console.log(state.inputs.username)
    };

    const handleChannelName = event => {
        setState({
            ...state,
            inputs: {...state.inputs, channel_name: event.target.value,}
        });
    };

    const handleTypeVariant = name => event => {
        setState({
            ...state,
            typeVariant: {
                ...state.typeVariant,
                [name]: event.target.checked,
            }
        });
    };

    return (
        <SecondaryLayout>
            <PageCaptionSpacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Typography component="h1" variant="h5" align="center" color="textPrimary">
                        Форма заказа
                    </Typography>

                    <form
                        noValidate
                        onSubmit={e => {
                            e.preventDefault();
                            alert('ОК! Повесь обработчик на кнопку или вызови нужную функцию изнутри обработчика формы')
                        }}>
                        <CompFormInputs inputs={state} handlers={{handleTypeVariant, handleUsernameInput, handleChannelName}}/>
                        <CompFormSummarize/>
                        <CompFormControls/>
                    </form>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
}

export default CompFormContainer
