import React from 'react';
import {connect} from "react-redux";
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import PageCaptionSpacer from "../common/PageCaptionSpacer";
import {FormWrapper, InnerFormSpacer, Title} from "../common/form_components";
import CompFormInputs from "./CompFormInputs/CompFormInputs";
import CompFormSummarize from "./CompFormSummarize/CompFormSummarize";
import CompFormControls from "./CompFormControls/CompFormControls";


function CompFormContainer(props) {
    const {userBalance} = props.user

    const [state, setState] = React.useState({
        typeVariant: {
            long: false,
            short: false,
        },
        inputs: {
            username: {
                label: "Your nickname",
                value: "",
            },
            channel_name: {
                label: "Channel",
                value: "",
            },
        },
    });

    const handleTypeVariant = name => event => {
        setState({
            ...state,
            typeVariant: {
                ...state.typeVariant,
                [name]: event.target.checked,
            }
        });
    };

    const handleInputs = event => {
        const inpName = event.target.id
        const inpValue = event.target.value

        setState({
            ...state,
            inputs: {
                ...state.inputs,
                [inpName]: {
                    ...state.inputs[inpName],
                    value: inpValue
                }
            }
        })

    }

    const onFormSubmit = e => {
        e.preventDefault();
        alert('ОК! Повесь обработчик на кнопку или вызови нужную функцию изнутри обработчика формы')
    }

    return (
        <SecondaryLayout>
            <PageCaptionSpacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Title text="Форма заказа"/>

                    <form noValidate onSubmit={onFormSubmit}>
                        <CompFormInputs inputs={state} handlers={{handleTypeVariant, handleInputs}}/>
                        <CompFormSummarize/>
                        <CompFormControls/>
                    </form>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
}

const mapStateToProps = (state) => ({
    user: state.user
})
const mapDispatchToProps = (dispatch) => ({})

export default connect(mapStateToProps, mapDispatchToProps)(CompFormContainer)
