import React from 'react';
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import FormGroup from "@material-ui/core/FormGroup";
import {CompVariantCheckbox} from "../../common/CompVariant";
import FormLabel from "@material-ui/core/FormLabel";
import Box from "@material-ui/core/Box";

function CompFormInputs(props) {
    const {typeVariant, inputs,} = props.inputs
    const {handleTypeVariant, handleInputs} = props.handlers

    return (
        <>
            <Grid container>
                <Grid item xs={12}>
                    <Box>
                        <FormLabel component="legend" style={{margin: '1rem 0',}}>Вариант отображения:</FormLabel>
                        <FormGroup row style={{justifyContent: 'center',}}>
                            <CompVariantCheckbox variant='long' handler={handleTypeVariant} checked={typeVariant.long}/>
                            <CompVariantCheckbox variant='short' handler={handleTypeVariant}
                                                 checked={typeVariant.short}/>
                        </FormGroup>
                    </Box>
                </Grid>
                {Object.entries(inputs).map(([inputName, inputInfo], id) => (
                    <Grid key={id} item xs={12}>
                        <Box mt={2}>
                            <TextField
                                required id={inputName} label="Label" variant="outlined"
                                fullWidth value={inputInfo,value} onChange={handleInputs}
                            />
                        </Box>
                    </Grid>
                ))}
            </Grid>
        </>
    )
}


export default CompFormInputs
