import React from 'react';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import PageCaptionSpacer from "../common/PageCaptionSpacer";
import Typography from "@material-ui/core/Typography";
import {FormWrapper, InnerFormSpacer} from "../common/form_components";
import CompFormInputs from "./CompFormInputs/CompFormInputs";
import CompFormSummarize from "./CompFormSummarize/CompFormSummarize";
import CompFormControls from "./CompFormControls/CompFormControls";


function CompFormContainer(props) {
    const [state, setState] = React.useState({
        compVariant: {
            long: false,
            short: false,
        },
        username: '',
    });


    const handleUsernameInput = name => event => {
        setState({
            ...state,
            username: event.target.value,
        });
    };

    const handleCompVariant = name => event => {
        setState({
            ...state,
            compVariant: {
                ...state.compVariant,
                [name]: event.target.checked,
            }
        });
    };

    return (
        <SecondaryLayout>
            <PageCaptionSpacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Typography component="h1" variant="h5" align="center" color="textPrimary">
                        Форма заказа
                    </Typography>

                    <form
                        noValidate
                        onSubmit={e => {
                            e.preventDefault();
                            alert('ОК! Повесь обработчик на кнопку или вызови нужную функцию изнутри обработчика формы')
                        }}>
                        <CompFormInputs inputs={{username, state}} handlers={{handleUsernameInput, handleCompVariant}}/>
                        <CompFormSummarize/>
                        <CompFormControls/>
                    </form>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
}

export default CompFormContainer
