import React, {Component} from 'react'
import Header from "../_layouts/_includes/header/Header";
import Footer from "../_layouts/_includes/footer/Footer";
import {withTheme} from '@material-ui/core/styles';
import styled from 'styled-components'

const Toolbar = styled.div`
    ${({theme}) => `
        min-height: ${theme.mixins.toolbar.minHeight}px;
    `}
`

const SteamBackground = styled.div`
    min-height: 100%;
    height: auto;
    position: relative;
    min-width: 950px;
    background-color: #000000;
    background-position: center top;
    background-repeat: no-repeat;
    background-image: url('https://steamcdn-a.akamaihd.net/steamcommunity/public/images/items/282760/4ee088bd6fdce882ea787242da79e01dfb150d77.jpg');
`

const ProfileHeaderBg = styled.div`
    position: relative;
    max-width: 1018px;
    min-height: 224px;
    padding-bottom: 16px;
    margin: 0 auto;
    background-repeat: no-repeat;
    background-position: center bottom;
`

const ProfileHeaderBgTexture = styled.div`
    max-width: 976px;
    min-height: 224px;
    margin: 0 auto;
    background-image: url('https://steamcommunity-a.akamaihd.net/public/images/profile/profile_header_bg_texture.jpg');
    background-repeat: no-repeat;
    background-position: center;
    box-shadow: 0 0 11px 2px black;
`

const ProfileHeader = styled.div`
    position: relative;
    max-width: 926px;
    margin: 0 auto;
    padding-top: 24px;
`
const ProfileHeaderContent = styled.div`
    position: relative;
    padding-top: 8px;
    color: #898989;
    font-size: 13px;
`

const ProfileHeaderCenteredPersona = styled.div`
    position: absolute;
    left: 186px;
    right: 268px;
    white-space: nowrap;
`

const PersonaName = styled.div`
    line-height: 40px;
    font-size: 24px;
    color: #ffffff;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    font-weight: 200;
`

const PlayerAvatarWrapper = styled.div`
    border-radius: 0;
    padding: 1px;
    background-color: #57cbde;
    background: linear-gradient( to bottom, rgba(83,164,196,1) 5%, rgba(69,128,151,1) 95%);
    filter: none;
    position: relative;
    width: 166px;
    height: 166px;
    float: left;
    margin-right: 18px;
`

const PlayerAvatarInner = styled.div`
    position: absolute;
    top: 2px;
    right: 2px;
    bottom: 2px;
    left: 2px;
`

const PlayerAvatar = styled.img`
    background: none;
    filter: none;
    display: block;
    padding: 0;
    width: 100%;
    height: 100%;
`

const ProfileHeaderSummary = styled.div`
    overflow: hidden;
    padding-right: 18px;
`


class ShowcaseContainer extends Component {

    constructor(props) {
        super(props)
        this.state = {}
    }

    render() {
        return (
            <>
                <Header/>
                <Toolbar theme={this.props.theme}/>


                <SteamBackground>
                    <ProfileHeaderBg>
                        <ProfileHeaderBgTexture>
                            <ProfileHeader>
                                <ProfileHeaderContent>
                                    <ProfileHeaderCenteredPersona>
                                        <PersonaName>
                                            <span>Nacho</span>
                                        </PersonaName>
                                    </ProfileHeaderCenteredPersona>
                                    <PlayerAvatarWrapper>
                                        <PlayerAvatarInner>
                                            <PlayerAvatar/>
                                        </PlayerAvatarInner>
                                    </PlayerAvatarWrapper>
                                    <ProfileHeaderSummary>
                                        <div className="persona_name persona_name_spacer" style={{fontSize: 24}}>
                                            <span className="actual_persona_name">&nbsp;</span>
                                        </div>
                                    </ProfileHeaderSummary>
                                </ProfileHeaderContent>
                            </ProfileHeader>
                        </ProfileHeaderBgTexture>
                    </ProfileHeaderBg>
                </SteamBackground>

                <Footer/>
            </>
        )
    }
}

export default withTheme(ShowcaseContainer)
