import React from 'react'
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Icon from "@material-ui/core/Icon";

import LongCompDefault from '../../assets/long_icon.svg'
import LongCompActive from '../../assets/long_icon_active.svg'
import LongCompDisabled from '../../assets/long_icon_disabled.svg'
import ShortCompDefault from '../../assets/short_icon.svg'
import ShortCompActive from '../../assets/short_icon_active.svg'
import ShortCompDisabled from '../../assets/short_icon_disabled.svg'


export function CompIcon(props) {
    return (
        <Icon style={{textAlign: 'center', fontSize: '7.5rem',}}>
            <img {...props} height="100%" width="100%" alt="Composition Icon"/>
        </Icon>
    );
}

export function CompVariantCheckbox(props) {
    const labelName = props.variant === 'long' ? 'Длинный' : 'Короткий'
    const icon = props.variant === 'long' ? LongCompDisabled : ShortCompDefault
    const checkedIcon = props.variant === 'long' ? LongCompActive : ShortCompActive

    return (
        <FormControlLabel
            control={
                <Checkbox
                    checked={props.checked}
                    icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                    onChange={props.handler(props.variant)}
                    value={props.variant} color="default" style={{borderRadius: 0,}}
                />
            }
            label={labelName}
            labelPlacement="bottom"
            disabled={props.disabled}
        />
    )
}
