import React from "react";
import styled from "styled-components";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import {TextField} from "@material-ui/core";
import Checkbox from "@material-ui/core/Checkbox";
import Radio from "@material-ui/core/Radio";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import Icon from "@material-ui/core/Icon";
import LongCompDefault from '../../assets/long_icon.svg'
import LongCompActive from '../../assets/long_icon_active.svg'
import ShortCompDefault from '../../assets/short_icon.svg'
import ShortCompActive from '../../assets/short_icon_active.svg'
import Grid from "@material-ui/core/Grid";
import Box from "@material-ui/core/Box";
import FormLabel from "@material-ui/core/FormLabel";
import FormGroup from "@material-ui/core/FormGroup";
import {Field, getIn} from "formik";
import Alert from "@material-ui/lab/Alert";
import Collapse from "@material-ui/core/Collapse";
import {InnerPreloader} from "./layout";

export const TextInput = styled(TextField)`
  label.Mui-focused {
    color: white;
  }
  .MuiOutlinedInput-root {
    border-radius: 0;
    &:hover fieldset {
      border-color: white;
    }
    &.focused fieldset {
      border-color: white;
    }
  }
  .MuiOutlinedInput-root.Mui-focused fieldset {
    border-color: white;
  }
`;
// #4CAF50 green
// #ef5350 red

export function Title({text, ...props}) {
    return (
        <Typography component="h1" variant="h5" align="center" color="textPrimary" gutterBottom={true}>
            {text}
        </Typography>
    )
}

export const FormWrapper = styled(Container)`
        width: auto;
        margin-left: ${({theme}) => theme.spacing(2)}px;
        margin-right: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(2) * 2)} {
            width: 600px;
            margin-left: auto;
            margin-right: auto;
        };
`

export const InnerFormSpacer = styled(Paper)`
        margin-bottom: ${({theme}) => theme.spacing(3)}px;
        padding: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(3) * 2)} {
            margin-bottom: ${({theme}) => theme.spacing(6)}px;
            padding: ${({theme}) => theme.spacing(3)}px;
        },
`

export const Btn = styled(Button)`
    margin-top: ${({theme}) => theme.spacing(3)}px;
    margin-left: ${({theme}) => theme.spacing(1)}px;
`

export const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-start;
                
        ${(props) => props.left && `justify-content: flex-start;`}
        ${(props) => props.center && `justify-content: center;`}
        ${(props) => props.right && `justify-content: flex-end;`}
`

export function CompIcon(props) {
    return (
        <Icon style={{textAlign: 'center', fontSize: '7.5rem',}}>
            <img {...props} height="100%" width="100%" alt="Composition Icon"/>
        </Icon>
    );
}

export const CompVariantCB = (props) => {
    // const icon = id === 'long' ? LongCompDefault : ShortCompDefault
    // const checkedIcon = id === 'long' ? LongCompActive : ShortCompActive

    return (
        <Field name={props.name}>
            {({field, form}) => (
                <FormControlLabel
                    control={
                        <Checkbox
                            {...props}
                            icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                            color="default" style={{borderRadius: 0,}}
                            checked={field.value.includes(props.value)}
                            onChange={form.handleChange} onBlur={form.handleBlur}
                        />
                    }
                    // label={label}
                    labelPlacement="bottom"
                />
            )}
        </Field>
    )
}

export const CompVariantRB = ({field, form, id, label, checked, disabled, ...props}) => {
    const icon = id === 'long' ? LongCompDefault : ShortCompDefault
    const checkedIcon = id === 'long' ? LongCompActive : ShortCompActive

    return (
        <FormControlLabel
            control={
                <Radio
                    icon={<CompIcon src={icon}/>} checkedIcon={<CompIcon src={checkedIcon}/>}
                    color="default" style={{borderRadius: 0,}}
                    id={id} name={field.name}
                    value={id} checked={checked}
                    onChange={form.handleChange} onBlur={form.handleBlur}
                    {...props}
                />
            }
            label={label}
            labelPlacement="bottom"
            disabled={disabled}
        />
    );
};

export const TypeVariantsWrapper = ({touched, errors, isFetching, ...props}) => {
    return (
        <Grid item xs={12}>
            <Box style={{backgroundColor: '#303030'}} p={1}>
                <FormLabel component="legend" style={{margin: '1rem 0', textAlign: 'center',}}>
                    Вариант отображения:
                </FormLabel>

                <FormGroup row style={{justifyContent: 'center',}}>
                    {
                        !!isFetching ?
                            <>
                                <InnerPreloader/>
                                <InnerPreloader/>
                            </>
                            :
                            props.children
                    }
                </FormGroup>

                <Collapse in={errors["typeVariant"] && touched["typeVariant"]}>
                    <Alert variant="filled" severity="warning">{errors["typeVariant"]}</Alert>
                </Collapse>
            </Box>
        </Grid>
    )
}


export const TVariants = ({type = "", touched, errors, isFetching, ...props}) => {
    return (
        <TypeVariantsWrapper
            touched={touched}
            errors={errors}
            isFetching={isFetching}
        >
            <CompVariantCB name="typeVariant" value="long"/>
            <CompVariantCB name="typeVariant" value="short"/>
        </TypeVariantsWrapper>
    )
}


export const TypeVariants = ({variant, values, touched, errors, isFetching, ...props}) => {
    return (
        <TypeVariantsWrapper
            touched={touched}
            errors={errors}
            isFetching={isFetching}
        >
            <Field
                component={CompVariantRB} name="typeVariant"
                id="long" label="Длинный"
                checked={variant === 1 ? "long" === values.typeVariant : variant === 2}
                disabled={variant === 3}
            />
            <Field
                component={CompVariantRB} name="typeVariant"
                id="short" label="Короткий"
                checked={variant === 1 ? "short" === values.typeVariant : variant === 3}
                disabled={variant === 2}
            />
        </TypeVariantsWrapper>
    )
}

export const Placeholder = ({field, form, ...props}) => {
    return (
        <Box mt={2} style={{backgroundColor: '#303030'}}>
            <TextInput
                required variant="outlined" fullWidth
                onChange={form.handleChange}
                {...field} {...props}
            />

            <Collapse in={getIn(form.errors, field.name) && getIn(form.touched, field.name)}>
                <Alert variant="filled" severity="warning">{getIn(form.errors, field.name)}</Alert>
            </Collapse>
        </Box>
    )
};

export const PlaceholderList = ({inputs, ...props}) => {
    return Object.keys(inputs).map((inputName, id) => {
            return (
                (
                    <Grid key={id} item xs={12}>
                        <Field name={`ph.${inputName}`} label={inputs[inputName].label} component={Placeholder}/>
                    </Grid>
                )
            )
        }
    )
}
