import React, {Component} from 'react'
import Typography from "@material-ui/core/Typography";
import {withRouter} from "react-router-dom";
import CompositionList from "./CompositionList";
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import Container from "@material-ui/core/Container";
import styled from "styled-components";
import {fetchList, clearList} from "../../../store/ducks/compositions/operations";
import Preloader from "../../common/Preloader";
import queryString from 'query-string'
import Pager from "./Pager";


import { MemoryRouter as Router } from 'react-router';
import { Link } from 'react-router-dom';
import Pagination from '@material-ui/lab/Pagination';
import PaginationItem from '@material-ui/lab/PaginationItem';


const CompListWrapper = styled(Container)`
        padding-top: ${({theme}) => theme.spacing(2)}px;
        padding-bottom: ${({theme}) => theme.spacing(2)}px;
        background-color: rgba(33,33,33,.95);
        border-radius: 20px;
`

const PageCaptionSpacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`

class CompositionsContainer extends Component {

    componentDidMount() {
        if (this.props.compositions.length === 0) {
            const search = queryString.parse(this.props.location.search)
            const uri = queryString.stringifyUrl({url: this.props.location.pathname, query: search});
            this.props.fetchList(uri)
        }
    }

    componentWillUnmount() {
        if (this.props.compositions.length > 0) {
            this.props.clearList()
        }
    }

    handlePageChange() {
        // this.handlePageChange.bind(this, item.page)

        // console.log(this.props.location)
        // const uri = queryString.stringifyUrl({
        //     url: this.props.location.pathname, query: {
        //         page: to,
        //     }
        // });
        // console.log(uri)

        // this.props.fetchList(uri)
    };

    render() {
        const {total, per_page} = this.props
        const countPages = Math.ceil(total / per_page)

        return (
            <SecondaryLayout>
                <PageCaptionSpacer>
                    <Container maxWidth="xl">
                        <Typography component="h1" variant="h5" align="left" color="textPrimary">
                            Листинг композиций
                        </Typography>
                    </Container>
                </PageCaptionSpacer>

                <CompListWrapper maxWidth="xl">
                    <Pager count={countPages} />
                    {
                        this.props.isFetching ?
                            <Preloader/> :
                            <CompositionList compositions={this.props.compositions}/>
                    }

                </CompListWrapper>

            </SecondaryLayout>
        )
    }
}

const mapStateToProps = (store) => ({
    compositions: store.compositions.compositions,
    isFetching: store.compositions.isFetching,
    total: store.compositions.total,
    per_page: store.compositions.per_page,
})

export default connect(mapStateToProps, {fetchList, clearList})(withRouter(CompositionsContainer))
