import React from 'react';
import {Field, withFormik} from 'formik';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {Spacer} from "../../common/layout";
import {
    FormWrapper,
    InnerFormSpacer,
    PlaceholderList,
    Title,
    TypeVariants
} from "../../common/form_components";
import Grid from "@material-ui/core/Grid";
import OrderFormSummarize from "./Summarize/Summarize";
import OrderFormControls from "./Controls/Controls";
import {compose} from "redux";
import {checkOnMax, checkOnMin, checkOnRequired} from "../../../store/utils/form_helpers";

const OrderFormWrapper = ({caption = "Безымянный", size = "xl", children, ...props}) => {
    return (
        <SecondaryLayout>
            <Spacer/>
            <FormWrapper maxWidth={size}>
                <InnerFormSpacer>
                    <Title text={caption}/>
                    <form onSubmit={props.handleSubmit} noValidate>
                        {children}
                    </form>
                </InnerFormSpacer>
            </FormWrapper>
        </SecondaryLayout>
    )
}

const OrderForm = props => {
    const {
        values, touched, errors, orderFormData: {typeVariant, ph, price, title}, handleSubmit, handleReset
    } = props;

    return (
        <OrderFormWrapper caption="Форма заказа" handleSubmit={handleSubmit}>
            <Grid container>

                {/*type varient*/}
                <TypeVariants variant={typeVariant} values={values}/>
                {/*inputs*/}
                {Object.keys(ph).length > 0 && (
                    <PlaceholderList inputs={ph}/>
                )}

            </Grid>
            <OrderFormSummarize price={price} title={title}/>
            <OrderFormControls onReset={handleReset} onBack={() => {
                props.history.goBack()
            }}/>
        </OrderFormWrapper>
    );
};

const getDefaultValues = ({typeVariant, ph, ...state}) => {
    let values = {
        typeVariant: typeVariant === 0 ? '' : typeVariant === 1 ? "long" : "short"
    }

    // inputs
    Object.keys(ph).forEach(keyName => {
        values[keyName] = ''
    })

    return values
}
const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default compose(
    connect(mapStateToProps, {}),
    withFormik({
        mapPropsToValues: (props) => (getDefaultValues(props.orderFormData)),

        // Custom sync validation
        validate: (values, {orderFormData: {ph}, ...props}) => {
            const errors = {};
            const rules = {}

            Object.keys(ph).forEach(keyName => {
                rules[keyName] = ph[keyName].rules
            })

            Object.keys(rules).forEach(keyName => {
                rules[keyName].split('|').forEach(rule => {
                    console.log(rules[keyName])
                    errors[keyName] = rules[keyName] && /^required$/ig.test(rule) === true && checkOnRequired(values[keyName])
                    errors[keyName] = rules[keyName] && /^min:\d{1,2}$/ig.test(rule) === true && checkOnMin()
                    errors[keyName] = rules[keyName] && /^max:\d{1,2}$/ig.test(rule) === true && checkOnMax()
                })
            })
            console.log(errors)

            return errors;
        },

        handleSubmit: (values, {setSubmitting, ...props}) => {
            setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                setSubmitting(false);
                // props.props.history.push(`${props.props.location.pathname}/success`)
            }, 1000);
        },

        displayName: 'OrderForm',
    })
)(OrderForm)
