import * as types from "./types";


export const initialState = {
    alias: null,
    title: null,
    typeVariant: null,
    price: null,
    ph: {},
    errors: {}
}

function compOrderFormReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.SET_COMPOSITION_FORM:
            return {
                ...state,
                isFetching: true,
                errors: {},
            }
        case types.SET_COMPOSITION_FORM_COMPLETED:

            return {
                ...state,
                alias: payload.link_alias,
                title: payload.title,
                price: payload.price,
                typeVariant: payload.type_variant,
                ph: payload.config,
                isFetching: false,
                errors: {}
            }
        case types.SET_COMPOSITION_FORM_FAILED:
            return {
                ...state,
                isFetching: false,
                errors: {
                    message: payload.data.message,
                    status: payload.status
                }
            }
        case types.CLEAR_COMP_FORM:
            return {
                ...state,
                alias: null,
                title: null,
                price: null,
                typeVariant: null,
                ph: {},
                isFetching: false,
                errors: {}
            }
        default:
            return state
    }
}


export default compOrderFormReducer
