import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import useMediaQuery from "@material-ui/core/useMediaQuery/useMediaQuery";


export const applyRootTheme = (themeColor) => {
    const prefersDarkMode = useMediaQuery(`(prefers-color-scheme: ${themeColor})`);

    return createMuiTheme({
        palette: {
            type: prefersDarkMode ? 'light' : 'dark',
            primary: {
                main: '#3f51b5',
                mainGradient: prefersDarkMode ?
                    "linear-gradient(0, rgb(238, 238, 238) 20%, rgb(46, 204, 113, .5) 50%, rgb(238, 238, 238) 100%)" :
                    "linear-gradient(0, rgb(66, 66, 66) 20%, rgb(155, 89, 182, .5) 50%, rgb(66, 66, 66) 100%)",
            },
            background: {
                default: prefersDarkMode ? '#eee' : '#424242',
            },
        },
    })
}
