import {initAppCompleted} from './actions'
import {authMe} from "../auth/actions";

const initApp = () => (dispatch, getState) => {
    const authMeProm = dispatch(authMe())
    Promise.all([authMeProm]).then(() => {
        dispatch(initAppCompleted())
    })

}

export {initApp}
