import SecondaryLayout from "../../layout/SecondaryLayout";
import {
    FormWrapper, Placeholder,
    TypeVariantsCB,
} from "../../common/form_components";
import React from "react";
import SuggestFormControls from "./Controls/Controls";
import Grid from "@material-ui/core/Grid";
import {PagePreloader} from "../../common/layout";


const SuggestForm = (props) => {
    const {
        touched, errors,
        handleSubmit, handleReset, handleBack, isSubmitting
    } = props;

    if (isSubmitting) return <PagePreloader opacity={75}/>
    else
        return (
            <SecondaryLayout>
                <FormWrapper caption="Cover letter to designers">
                    <form noValidate onSubmit={handleSubmit}>
                        <Grid container>

                            {/*type variant*/}
                            <TypeVariantsCB
                                touched={touched} errors={errors}
                            />
                            {/*<TextInput*/}
                            <Placeholder name="description" label="Description" multiline/>

                        </Grid>
                        <SuggestFormControls
                            onReset={handleReset}
                            onBack={handleBack}
                        />
                    </form>
                </FormWrapper>
            </SecondaryLayout>
        );
}

export default SuggestForm
