import React from 'react';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import Switch from "@material-ui/core/Switch";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormGroup from "@material-ui/core/FormGroup";
import Checkbox from "@material-ui/core/Checkbox";
import LongCompIcon from "../common/LongCompIcon";
import ShortCompIcon from "../common/ShortCompIcon";
import LongCompDefault from '../../assets/long_icon.svg'
import LongCompActive from '../../assets/long_icon_active.svg'
import LongCompDisabled from '../../assets/long_icon_disabled.svg'
import ShortCompDefault from '../../assets/short_icon.svg'
import ShortCompActive from '../../assets/short_icon_active.svg'
import ShortCompDisabled from '../../assets/short_icon_disabled.svg'
import PageCaptionSpacer from "../common/PageCaptionSpacer";
import {Btn, ButtonsGroup, FormWrapper, InnerFormSpacer} from "../common/form_components";

function SuggestForm() {
    const [state, setState] = React.useState({
        withBackground: false,
        long: false,
        short: false,
    });
    const [value, setValue] = React.useState('');
    const [name, setName] = React.useState('');


    const handleUsername = event => {
        setName(event.target.value);
    };

    const handleDesc = event => {
        setValue(event.target.value);
    };

    const handleChange = name => event => {
        setState({...state, [name]: event.target.checked});
    };

    return (
        <>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <TextField
                        required id="username" label="Никнейм"
                        fullWidth value={name} onChange={handleUsername}
                    />
                    <TextField
                        required id="description" label="Описание"
                        fullWidth value={value} onChange={handleDesc}
                        multiline
                    />
                </Grid>
                <Grid item xs={12}>
                    <FormGroup row>
                        <FormControlLabel
                            control={
                                <Checkbox
                                    checked={state.long}
                                    icon={<LongCompIcon src={LongCompDefault}/>}
                                    checkedIcon={<LongCompIcon src={LongCompActive}/>}
                                    onChange={handleChange('long')} value="long"
                                    color="default"
                                    style={{borderRadius: 0,}}
                                />
                            }
                            label="Длинный"
                            labelPlacement="bottom"
                        />
                        <FormControlLabel
                            control={
                                <Checkbox
                                    checked={state.short}
                                    icon={<ShortCompIcon src={ShortCompDefault}/>}
                                    checkedIcon={<ShortCompIcon src={ShortCompActive}/>}
                                    onChange={handleChange('short')} value="short"
                                    color="default"
                                    style={{borderRadius: 0,}}
                                />
                            }
                            label="Короткий"
                            labelPlacement="bottom"
                        />
                    </FormGroup>
                </Grid>
                <Grid item xs={12}>
                    <FormGroup row>
                        <FormControlLabel
                            control={
                                <Switch checked={state.withBackground} onChange={handleChange('withBackground')}
                                        value="withBackground" inputProps={{'aria-label': 'default checkbox'}}
                                        color="default"
                                />
                            }
                            label="Фон"
                        />
                        {state.withBackground && (
                            <TextField required id="background" name="background" label="Адресная ссылка"
                                       fullWidth color="primary"
                            />
                        )}
                    </FormGroup>
                </Grid>
            </Grid>
        </>
    )
}

function SuggestContainer() {

    return (
        <SecondaryLayout>
            <PageCaptionSpacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>

                    <Typography component="h1" variant="h5" align="center" color="textPrimary">
                    <ButtonsGroup>
                        <Btn variant="outlined">Назад</Btn>
                    </ButtonsGroup>
                        Письмо дизайнеру
                    </Typography>
                    <SuggestForm/>
                    <Grid container>
                        <Grid item xs>
                            <ButtonsGroup>
                                <Btn variant="outlined">Назад</Btn>
                            </ButtonsGroup>
                        </Grid>
                        <Grid item xs>
                            <ButtonsGroup right>
                                <Btn variant="contained">Предложить</Btn>
                            </ButtonsGroup>
                        </Grid>
                    </Grid>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
}

export default SuggestContainer
