import {LOGIN, LOGOUT} from "../actions/AuthActions";
import RAvatar from "../../assets/img/my_avatar.jpg"

const user = {
    user_id: null,
    steamid: null,
    nickname: null,
    avatar: null,
    balance: null,
    group: null,
    registered_at: null,
    visited_at: null,
};

const initialState = {
    isAuthenticated : false,
    isAdmin: false,
    user
};

export function userReducer(state = initialState, action) {
    switch (action.type) {
        case LOGIN:
            return {...state, isAuthenticated: action.payload}
        case LOGOUT:
            return {...state, isAuthenticated: action.payload}

        default:
            return state
    }
}
