import React, {Component} from 'react';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {FormWrapper, InnerFormSpacer, Title} from "../../common/form_components";
import OrderFormInputs from "./Inputs/Inputs";
import OrderFormSummarize from "./Summarize/Summarize";
import OrderFormControls from "./Controls/Controls";
import {Spacer} from "../../common/layout";
import {withFormik} from 'formik';

const MyForm = props => {
    const {
        values, touched, errors,
        handleChange, handleBlur, handleSubmit,
    } = props;
    return (
        <SecondaryLayout>
            <Spacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Title text="Форма заказа"/>
                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.name}
                            name="name"
                        />
                        {errors.name && touched.name && <div id="feedback">{errors.name}</div>}
                        <button type="submit">Submit</button>
                    </form>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
};

const MyEnhancedForm = withFormik({
    mapPropsToValues: () => ({name: ''}),

    // Custom sync validation
    validate: values => {
        const errors = {};

        if (!values.name) {
            errors.name = 'Required';
        }

        return errors;
    },

    handleSubmit: (values, {setSubmitting}) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
        }, 1000);
    },

    displayName: 'BasicForm',
})(MyForm);

const mapStateToProps = (state) => ({
    user: state.auth,
    compData: state.compOrderForm
})

export default connect(mapStateToProps, {})(MyEnhancedForm)


const getDefaultState = (composition) => {
    let typeVariant = {
        long: {
            value: composition.typeVariant === 1,
            disabled: composition.typeVariant === 2,
        },
        short: {
            value: composition.typeVariant === 2,
            disabled: composition.typeVariant === 1,
        },
    }
    let inputs = {}

    if (Object.entries(composition.inputs).length > 0) {
        inputs = {
            ...composition.inputs
        }

        Object.keys(inputs).forEach((propName) => {
            inputs[propName] = {
                ...composition.inputs[propName],
                value: '',
            }
        })
    }

    return ({
        typeVariant,
        inputs,
    })
}


class OrderFormContainer extends Component {
    constructor(props) {
        super(props)
        this.state = getDefaultState(props.compData)
    }

    componentDidMount() {
    }

    handleTypeVariant = name => event => {
        this.setState({
            ...this.state,
            typeVariant: {
                ...this.state.typeVariant,
                [name]: {
                    ...this.state.typeVariant[name],
                    value: event.target.checked,
                }
            }
        });
    }

    handleInputs = event => {
        const inpName = event.target.id
        const inpValue = event.target.value

        this.setState({
            ...this.state,
            inputs: {
                ...this.state.inputs,
                [inpName]: {
                    ...this.state.inputs[inpName],
                    value: inpValue
                }
            }
        })

    }

    onClear = () => {
        this.setState(getDefaultState(this.props.compData))
        // clear session / localStorage (action)
    }

    onBack = () => {
        this.props.history.goBack()
    }

    onSubmit = e => {
        e.preventDefault();
        // валидация данных в стейте, затем отправка на сервер
        this.props.history.push(`${this.props.location.pathname}/success`)
    }

    render() {
        return (
            <SecondaryLayout>
                <Spacer/>

                <FormWrapper maxWidth="xl">
                    <InnerFormSpacer>
                        <Title text="Форма заказа"/>

                        <form noValidate onSubmit={this.onSubmit}>
                            <OrderFormInputs inputs={this.state} handlers={{
                                handleTypeVariant: this.props.compData.typeVariant === 0 ? this.handleTypeVariant : () => {
                                },
                                handleInputs: this.handleInputs
                            }}/>
                            <OrderFormSummarize price={this.props.compData.price} title={this.props.compData.title}/>
                            <OrderFormControls onClear={this.onClear} onBack={this.onBack}/>
                        </form>

                    </InnerFormSpacer>
                </FormWrapper>

            </SecondaryLayout>
        );
    }
}
