import * as types from "./types";
import Http from '../../utils/Http'

const user = {
    user_id: null,
    steamid: null,
    nickname: null,
    avatar: null,
    balance: null,
    group: null,
};

const initialState = {
    isAuthenticated: false,
    user
};

export default function authReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.AUTH_LOGIN:
            return authLogin(state, payload);
        case types.AUTH_CHECK:
            return checkAuth(state);
        case types.AUTH_LOGOUT:
            return logout(state);
        default:
            return state;
    }
};

const authLogin = (state, payload) => {
    console.log(payload)
    const jwtToken = payload.token;
    const user = payload.user[0];
    localStorage.setItem('jwt_token', jwtToken);
    Http.defaults.headers.common['Authorization'] = `Bearer ${jwtToken}`;

    return {
        ...state,
        isAuthenticated: true,
        user
    }
};

const checkAuth = (state) => {
    const hasJwt = !!localStorage.getItem('jwt_token')
    if (hasJwt) {
        Http.defaults.headers.common['Authorization'] = `Bearer ${localStorage.getItem('jwt_token')}`;
    }

    return {
        ...state,
        isAuthenticated: hasJwt,
    };
};

const logout = (state) => {
    localStorage.removeItem('jwt_token');

    return {
        ...state,
        isAuthenticated: false,
        user
    }
};
