import React from 'react';

import {withRouter} from "react-router-dom";
import Button from "@material-ui/core/Button";

class LoginButton extends React.Component {
    onHandleLogin() {
        const popupWindow = window.open(process.env.REACT_APP_API_URL + '/api/auth/steam/handle', '', 'width=800, height=600');
        if (window.focus)
            popupWindow.focus()
    }

    handler() {
        const res = 1
        this.props.login(res)
    }

    componentDidMount() {
        window.addEventListener('message', event => {
            if (event.origin !== process.env.REACT_APP_API_URL) return;

            const {response: res} = event.data;
            this.props.login(res)
        });
    }

    render() {
        return (
            <>
                <div onClick={this.handler}>text</div>
                <Button onClick={this.onHandleLogin} color="inherit">Войти</Button>
            </>
        );
    }
}

export default withRouter(LoginButton)
