import React from 'react';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import Container from "@material-ui/core/Container";
import styled from "styled-components";
import Switch from "@material-ui/core/Switch";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormGroup from "@material-ui/core/FormGroup";
import Checkbox from "@material-ui/core/Checkbox";
import {Favorite, FavoriteBorder} from "@material-ui/icons";
import SvgIcon from "@material-ui/core/SvgIcon";
import LongCompDefault from '../../assets/long_icon_alpha.svg'
import ShortCompDefault from '../../assets/short_icon_alpha.svg'
import Icon from "@material-ui/core/Icon";

const Btn = styled(Button)`
    margin-top: ${({theme}) => theme.spacing(3)}px;
    margin-left: ${({theme}) => theme.spacing(1)}px;
`

const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-end;
`

const InnerFormSpacer = styled(Paper)`
        margin-bottom: ${({theme}) => theme.spacing(3)}px;
        padding: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(3) * 2)} {
            margin-bottom: ${({theme}) => theme.spacing(6)}px;
            padding: ${({theme}) => theme.spacing(3)}px;
        },
`

const SuggestWrapper = styled(Container)`
        width: auto;
        margin-left: ${({theme}) => theme.spacing(2)}px;
        margin-right: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(2) * 2)} {
            width: 600px;
            margin-left: auto;
            margin-right: auto;
        };
`

const PageCaptionSpacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`


function LongCompIcon({...props}) {
    return (
        <Icon>
            <img src={LongCompDefault} alt="Long Composition"/>
        </Icon>
    );
}

function ShortCompIcon({...props}) {
    return (
        <Icon>
            <img src={ShortCompDefault} alt="Short Composition"/>
        </Icon>
    );
}

function SuggestForm() {
    const [state, setState] = React.useState({
        withBackground: false,
        long: false,
        short: false,
    });
    const [value, setValue] = React.useState('');
    const [name, setName] = React.useState('');


    const handleUsername = event => {
        setName(event.target.value);
    };

    const handleDesc = event => {
        setValue(event.target.value);
    };

    const handleChange = name => event => {
        setState({...state, [name]: event.target.checked});
    };

    // description, type (two checkboxes), background url input (if checkbox true)
    return (
        <>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <div>
                        <TextField required id="username" label="Username" value={name} onChange={handleUsername}
                                   fullWidth autoComplete="Username" color="primary"/>
                        <TextField
                            required id="description" label="Description"
                            multiline rowsMax="4" value={value} onChange={handleDesc}
                        />
                    </div>
                    <FormGroup row>
                        <FormControlLabel
                            control={
                                <Checkbox checked={state.long} onChange={handleChange('long')} value="long"/>
                            }
                            label="Long"
                        />
                        <FormControlLabel
                            control={
                                <Checkbox checked={state.short} onChange={handleChange('short')} value="short"
                                />
                            }
                            label="Short"
                        />
                        <FormControlLabel
                            control={
                                <Checkbox
                                    icon={<LongCompIcon/>}
                                    checkedIcon={<Favorite/>}
                                    value="checkedH"
                                />
                            }
                            label="Long"
                        />
                        <FormControlLabel
                            control={
                                <Checkbox
                                    icon={<ShortCompIcon/>}
                                    checkedIcon={<Favorite/>}
                                    value="checkedH"
                                />
                            }
                            label="Long"
                        />
                    </FormGroup>
                    <FormGroup row>
                        <FormControlLabel
                            control={
                                <Switch checked={state.withBackground} onChange={handleChange('withBackground')}
                                        value="withBackground" inputProps={{'aria-label': 'default checkbox'}}
                                        color="default"
                                />
                            }
                            label="With steam background"
                        />
                        {state.withBackground && (
                            <TextField required id="background" name="background" label="Url"
                                       fullWidth autoComplete="Background" color="primary"
                            />
                        )}
                    </FormGroup>
                </Grid>
            </Grid>
        </>
    )
}

function SuggestContainer() {

    return (
        <SecondaryLayout>
            <PageCaptionSpacer>
                <Container maxWidth="xl">
                    <Typography component="h1" variant="h5" align="left" color="textPrimary">
                        Письмо дизайнерам
                    </Typography>
                </Container>
            </PageCaptionSpacer>

            <SuggestWrapper maxWidth="xl">
                <InnerFormSpacer>

                    <SuggestForm/>
                    <ButtonsGroup>
                        <Btn variant="contained">Предложить</Btn>
                    </ButtonsGroup>

                </InnerFormSpacer>
            </SuggestWrapper>

        </SecondaryLayout>
    );
}

export default SuggestContainer
