import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Typography from "@material-ui/core/Typography";
import Toolbar from '@material-ui/core/Toolbar';
import {connect} from "react-redux";
import styled from 'styled-components'

import Logo from "./Logo";
import LoginButton from "./LoginButton";
import NavProfile from "./NavProfile";
import {login, logout} from "../../../store/ducks/auth/actions";

const StyledAppBar = styled(AppBar)`
    z-index: ${({theme}) => theme.zIndex.drawer + 1};
`

function Header({userInfo, ...props}) {

    // console.log(localStorage.removeItem('authToken'))

    const ProfileCtx = (userInfo.isAuthenticated ?
            <NavProfile avatar={userInfo.user.avatar} nickname={userInfo.user.nickname} logout={props.logout}/> :
            <LoginButton login={() => {alert('ok')}}/>
    )

    return (
        <StyledAppBar position="fixed" color="default">
            <Toolbar disableGutters style={{padding: '0 4px',}}>
                <Typography style={{flexGrow: 1,}}>
                    <Logo/>
                </Typography>
                {ProfileCtx}
            </Toolbar>
        </StyledAppBar>
    );
}

const mapStateToProps = (store) => ({
    userInfo: store.auth
})

const mapStateToDispatch = (dispatch) => ({
    login: () => dispatch(login()),
    logout: () => dispatch(logout()),
})

export default connect(mapStateToProps, mapStateToDispatch)(Header)
