import React, {useEffect, useRef} from 'react';
import Button from "@material-ui/core/Button";


function LoginButton({login, ...props}) {
    const onHandleLogin = () => {
        const popupWindow = window.open(process.env.REACT_APP_API_URL + '/api/auth/steam/handle', '_target', 'width=800, height=600');
        if (window.focus) popupWindow.focus();
    }

    useEffect(() => {
        function handler(event) {
            if (event.origin === process.env.REACT_APP_API_URL) {
                console.log(event.data)
            } else {
                return
            }


            // const {token, ok} = event.data;
            //
            // if (ok) {
            //     localStorage.setItem('authToken', token);
            //     console.log(token);
            // }
        }

        // subscribe event
        window.addEventListener("message", handler);
        return () => {
            // unsubscribe event
            window.removeEventListener("message", handler);
        };
    }, []);

    return (
        <Button onClick={() => onHandleLogin()} color="inherit">Войти</Button>
    );
}

export default LoginButton
